import { Component, OnInit } from '@angular/core';
import { FormBuilder } from '@angular/forms';
import { RideService } from '../../services/ride.service';
import { Ride, VehicleType } from '../../models/ride.model';

@Component({
  selector: 'app-pick-ride',
  templateUrl: './pick-ride.component.html',
  styleUrls: ['./pick-ride.component.css']
})
export class PickRideComponent implements OnInit {
  vehicleTypes: (VehicleType | 'All')[] = ['All', 'Bike', 'Car'];
  rides: Ride[] = [];
  msg: { type: 'success' | 'error'; text: string } | null = null;

  filterForm = this.fb.group({
    employeeId: [''],
    vehicleType: ['All' as VehicleType | 'All'],
    timeReference: [''], // optional manual time else current time
  });

  constructor(private fb: FormBuilder, private rideService: RideService) {}

  ngOnInit(): void {
    this.refresh();
  }

  refresh() {
    const vt = this.filterForm.value.vehicleType as VehicleType | 'All' | undefined;
    const time = (this.filterForm.value.timeReference || undefined) as string | undefined;
    const emp = (this.filterForm.value.employeeId || '').trim();

    this.rides = this.rideService.listRides({
      vehicleType: vt,
      timeReference: time,
      excludeOwner: emp || undefined,
      onlyWithSeats: true
    });
  }

  book(ride: Ride) {
    this.msg = null;
    const emp = (this.filterForm.value.employeeId || '').trim();
    if (!emp) {
      this.msg = { type: 'error', text: 'Enter your Employee ID to book.' };
      return;
    }
    const res = this.rideService.bookRide(ride.id, emp);
    if (res.ok) {
      this.msg = { type: 'success', text: 'Ride booked successfully!' };
      this.refresh();
    } else {
      this.msg = { type: 'error', text: res.error };
    }
  }
}
<div class="card">
  <h2>Pick a Ride</h2>

  <form [formGroup]="filterForm" (ngSubmit)="refresh()" class="filters" novalidate>
    <label>
      Your Employee ID
      <input formControlName="employeeId" placeholder="E.g. EMP789" />
    </label>

    <label>
      Vehicle Type
      <select formControlName="vehicleType">
        <option *ngFor="let v of vehicleTypes" [value]="v">{{ v }}</option>
      </select>
    </label>

    <label>
      Time Reference (HH:mm)
      <input type="time" step="60" formControlName="timeReference" />
    </label>

    <button type="submit">Search</button>
  </form>

  <p *ngIf="msg" [class.success]="msg.type==='success'" [class.error]="msg.type==='error'">
    {{ msg.text }}
  </p>

  <!-- <div *ngIf="rides.length === 0" class="empty">No rides match within +/- 60 minutes.</div> -->

  <div class="ride-list" *ngIf="rides.length > 0">
    <div class="ride" *ngFor="let r of rides">
      <div class="info">
        <div><strong>Time:</strong> {{ r.time }}</div>
        <div><strong>From:</strong> {{ r.pickupPoint }} <strong>To:</strong> {{ r.destination }}</div>
        <div><strong>Vehicle:</strong> {{ r.vehicleType }} ({{ r.vehicleNo }})</div>
        <div><strong>Vacant Seats:</strong> {{ r.vacantSeats }}</div>
        <div><strong>Owner:</strong> {{ r.ownerEmployeeId }}</div>
      </div>
      <div class="actions">
        <button (click)="book(r)">Book</button>
      </div>
    </div>
  </div>
</div>
