import { Component, OnInit } from '@angular/core';
import { CommonModule } from '@angular/common';
import { FormBuilder, ReactiveFormsModule } from '@angular/forms';
import { Ride, VehicleType } from '../../models/ride.model';
import { RideService } from '../../services/ride.service';

@Component({
  selector: 'app-ride-list',
  templateUrl: './ride-list.component.html',
  styleUrls: ['./ride-list.component.css'],
  standalone: true,
  imports: [CommonModule, ReactiveFormsModule]
})
export class RideListComponent implements OnInit {
  vehicleTypes: Array<VehicleType | 'All'> = ['All', 'Bike', 'Car'];

  filters = this.fb.group({
    search: [''],
    vehicleType: ['All' as VehicleType | 'All'],
    onlyWithSeats: [false],
    timeReference: ['']
  });

  rides: Ride[] = [];

  constructor(private fb: FormBuilder, private rideService: RideService) {}

  ngOnInit(): void {
    this.apply();
    this.filters.valueChanges.subscribe(() => this.apply());
  }

  private apply() {
    const f = this.filters.value;
    const base = this.rideService.listRides({
      vehicleType: (f.vehicleType as any) || 'All',
      timeReference: f.timeReference || undefined,
      onlyWithSeats: !!f.onlyWithSeats,
    });

    const term = (f.search || '').toString().trim().toLowerCase();
    this.rides = term
      ? base.filter(r =>
          r.ownerEmployeeId.toLowerCase().includes(term) ||
          r.vehicleNo.toLowerCase().includes(term) ||
          r.pickupPoint.toLowerCase().includes(term) ||
          r.destination.toLowerCase().includes(term)
        )
      : base;
  }
}
<section>
  <h2>Available Rides</h2>

  <form [formGroup]="filters" class="filters">
    <input type="text" placeholder="Search (owner, vehicle no, pickup, destination)" formControlName="search" />

    <select formControlName="vehicleType">
      <option *ngFor="let vt of vehicleTypes" [value]="vt">{{ vt }}</option>
    </select>

    <label class="inline">
      <input type="checkbox" formControlName="onlyWithSeats" /> Only rides with seats
    </label>

    <label class="inline">
      Time (HH:mm)
      <input type="time" formControlName="timeReference" />
    </label>
  </form>

  <div *ngIf="rides.length === 0" class="empty">No rides found for the selected filters.</div>

  <ul class="ride-list">
    <li *ngFor="let r of rides" class="ride">
      <div class="row1">
        <span class="vt" [class.bike]="r.vehicleType==='Bike'" [class.car]="r.vehicleType==='Car'">{{ r.vehicleType }}</span>
        <strong>{{ r.time }}</strong>
        <span>Seats: {{ r.vacantSeats }}</span>
      </div>
      <div class="row2">
        <span>Owner: {{ r.ownerEmployeeId }}</span>
        <span>Vehicle: {{ r.vehicleNo }}</span>
      </div>
      <div class="row3">
        <span>{{ r.pickupPoint }} → {{ r.destination }}</span>
      </div>
    </li>
  </ul>
</section>
